exports.mysql_real_escape_string  = function (stringToSanitize) 
{
	// Cleaning the string by appending "\" to 
	// \x00, \n, \r, \, ', ", and \x1a	
	return stringToSanitize.replace(/\x00/g, "\\x00")
    			       .replace(/[\n]/g, "\\n")
			       .replace(/[\r]/g, "\\r")
			       .replace(/[\\]/g, "\\\\")
                               .replace(/[\\"']/g, "\\$&")			       
			       .replace(/\x1a/g, "\\x1a");
};
